packet_marker_size = 4
message_marker_size = 14


def marker_start(size, enc):
    start = 0
    while start + size < len(enc):
        marker = set(enc[start: start + size])
        if len(marker) == size:
            break
        start += 1
    return start + size


if __name__ == '__main__':
    msg = " "
    print(marker_start(packet_marker_size, msg))
    print(marker_start(message_marker_size, msg))

